$(function(){$('.td-content a[href]').each(function(){const t=$(this),e=t.attr('href');if(e.startsWith('http://')||e.startsWith('https://')){if(e.endsWith('.zip')||e.endsWith('.gz')||e.endsWith('.dmg'))return;t.attr('target','_blank'),t.append($('<span>').addClass('fas fa-external-link-alt ml-1'))}});const e=$('#supporters');e.length>0&&$.ajax('https://pqrs.org/supporters/supporters.json').then(s=>{let t=$('<div>').addClass('supporters'),n=null;s.forEach(s=>{n!==null&&s.avatar.scale!==n&&(e.append(t),t=$('<div>').addClass('supporters')),n=s.avatar.scale;const i=s.name!==''?s.name:s.login,a=$('<img>').attr('src',s.avatar.url).attr('alt',i).attr('width',80*s.avatar.scale).attr('height',80*s.avatar.scale),r=10*s.avatar.scale+'px',c=5*s.avatar.scale+'px',o=$('<a>').attr('href','https://github.com/'+s.login).attr('target','_blank').css({width:100*s.avatar.scale+'px',padding:r+' '+c}).append(a);switch(o.append($('<div>').text('@'+s.login)),s.name!==''?o.append($('<div>').text(s.name)):o.append($('<div>').css({height:'15px'})),n){case 4:o.css({fontSize:20});break;case 2:o.css({fontSize:16});break}s.tier>=40?o.addClass('gold'):s.tier>=20&&o.addClass('silver'),t.append(o)}),e.append(t)},t=>{e.append($('<div>').addClass('alert alert-danger').text('Error: Failed to fetch the sponsors list.'))})})