$(function(){$('.td-content a[href]').each(function(){const b=$(this),a=b.attr('href');if(a.startsWith('http://')||a.startsWith('https://')){if(a.endsWith('.zip')||a.endsWith('.gz')||a.endsWith('.dmg'))return;b.attr('target','_blank'),b.append($('<span>').addClass('fas fa-external-link-alt ml-1'))}});const a=$('#supporters');a.length>0&&$.ajax('https://pqrs.org/supporters/supporters.json').then(d=>{let b=$('<div>').addClass('supporters'),c=null;d.forEach(d=>{c!==null&&d.avatar.scale!==c&&(a.append(b),b=$('<div>').addClass('supporters')),c=d.avatar.scale;const f=d.name!==''?d.name:d.login,g=$('<img>').attr('src',d.avatar.url).attr('alt',f).attr('width',80*d.avatar.scale).attr('height',80*d.avatar.scale),h=10*d.avatar.scale+'px',i=5*d.avatar.scale+'px',e=$('<a>').attr('href','https://github.com/'+d.login).attr('target','_blank').css({width:100*d.avatar.scale+'px',padding:h+' '+i}).append(g);e.append($('<div>').text('@'+d.login)),d.name!==''?e.append($('<div>').text(d.name)):e.append($('<div>').css({height:'15px'})),b.append(e)}),a.append(b)},b=>{a.append($('<div>').addClass('alert alert-danger').text('Error: Failed to fetch the sponsors list.'))})})